monitoring_scripts.generate_performance_report.py:
#!/usr/bin/env python3
import json
import statistics
from collections import defaultdict

# Parse performance logs
timings = defaultdict(list)

with open('.claude/performance.log', 'r') as f:
    for line in f:
        parts = line.strip().split(',')
        if len(parts) >= 3:
            operation = parts[0]
            timings[operation].append(float(parts[2]))

# Calculate statistics
report = {}
for op, times in timings.items():
    if times:
        report[op] = {
            'count': len(times),
            'mean': statistics.mean(times),
            'median': statistics.median(times),
            'p95': statistics.quantiles(times, n=20)[18] if len(times) > 20 else max(times)
        }

# Save report
with open('.claude/performance_report.json', 'w') as f:
    json.dump(report, f, indent=2)

print('Performance report generated')

monitoring_scripts.identify_bottlenecks.py:
#!/usr/bin/env python3
import json

# Load performance report
with open('.claude/performance_report.json', 'r') as f:
    report = json.load(f)

# Identify bottlenecks
bottlenecks = []

for op, stats in report.items():
    if stats['p95'] > 1000:  # Operations taking >1 second at P95
        bottlenecks.append({
            'operation': op,
            'p95_time': stats['p95'],
            'recommendation': 'Consider optimization or caching'
        })

if bottlenecks:
    print('Performance bottlenecks detected:')
    for b in bottlenecks:
        print(f"  - {b['operation']}: {b['p95_time']}ms (P95)")
        print(f"    Recommendation: {b['recommendation']}")
else:
    print('No significant bottlenecks detected')
